import React from 'react';
import './MainPage.scss'
import PropTypes from 'prop-types';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import CssBaseline from '@material-ui/core/CssBaseline';
import useScrollTrigger from '@material-ui/core/useScrollTrigger';
import Box from '@material-ui/core/Box';
import Grid from '@material-ui/core/Grid';
import FoodList from "../components/FoodList";
import Header from "../components/Header/Header";

function ElevationScroll(props) {
  const { children, window } = props;
  // Note that you normally won't need to set the window ref as useScrollTrigger
  // will default to window.
  // This is only being set here because the demo is in an iframe.
  const trigger = useScrollTrigger({
    disableHysteresis: true,
    threshold: 0,
    target: window ? window() : undefined,
  });

  return React.cloneElement(children, {
    elevation: trigger ? 4 : 0,
  });
}

ElevationScroll.propTypes = {
  children: PropTypes.element.isRequired,
  /**
   * Injected by the documentation to work in an iframe.
   * You won't need it on your project.
   */
  window: PropTypes.func,
};

function MainPage(props) {
  return (
      <React.Fragment>
        <CssBaseline />
        <ElevationScroll {...props}>
          <AppBar>
            <Toolbar>
                <Header/>
            </Toolbar>
          </AppBar>
        </ElevationScroll>
        <Toolbar />
        <Grid  spacing={3}>
          <Box my={2}>
          <div className='main'>
            <div className={'top-slider'}>
                <div className="bg-image">
                    <img src={topSlider1}/>
                </div>
                <div className='slider-details'>
                    <h1 className='title'>Breakfast Special</h1>
                    <p className='sub'>
                        Enjoy $10 Off When you order two or more of Blueberry Pancake Breakfast
                    </p>

                    <button className='more-btn'>
                        use <span>Breakfast Special</span>
                    </button>
                </div>
            </div>
            <div className='page-body'>
                <div className='order-option-bar'>
                    <a className="options">
                        <div className="mode">ASAP Pickup</div>
                        <div className="sub">395 Santa Monica Pier</div>
                    </a>
                    <button className='change'>
                        change
                    </button>

                    <div className="type-select">
                        <div className="item">Delivery</div>
                        <span className="seperator">or</span>
                        <div className="item">Pickup</div>
                    </div>
                    <div className="divider"></div>
                    <a className="catering">Catering</a>
                </div>

            <FoodList/>
            </div>
        </div>
          </Box>
        </Grid>
      </React.Fragment>
  );
}

export default MainPage;
